mapper.select:
select

  distinct


from t_order

  


  order by ${orderByClause}


  limit #{limitOffset} , #{limitRows}

SELECT
count(torder.id)
FROM
t_order AS torder
LEFT JOIN t_product AS tprod ON torder.product_id = tprod.id
LEFT JOIN t_user AS tuser ON tuser.id = torder.user_id
WHERE
torder.deleted = 0
AND torder.`status` IN (0, 1, 2)

	AND torder.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


	AND torder.`code` = #{orderNo,jdbcType=VARCHAR}


	AND tuser.nickname = #{userName,jdbcType=VARCHAR}


	AND tprod.`name` = #{productName,jdbcType=VARCHAR}

SELECT tuser.nickname AS userNickName, tuser.open_id AS openId, torder.`code` AS orderNo, tprod.`code` AS productCode, tprod.`name` AS productName, torder.buy_amount AS buyAmount, torder.`status` AS orderStatus, torder.create_time AS createTime FROM t_order AS torder LEFT JOIN t_product AS tprod ON torder.product_id = tprod.id LEFT JOIN t_user AS tuser ON tuser.id = torder.user_id WHERE torder.deleted = 0 AND torder.`status` IN (0, 1, 2)
AND torder.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
AND torder.`code` = #{orderNo,jdbcType=VARCHAR}
AND tuser.nickname = #{userName,jdbcType=VARCHAR}
AND tprod.`name` = #{productName,jdbcType=VARCHAR}
ORDER BY torder.create_time desc

SELECT
	count(torder.id)
FROM
t_order AS torder
LEFT JOIN t_product AS tprod ON torder.product_id = tprod.id
LEFT JOIN t_user AS tuser ON tuser.id = torder.user_id
WHERE
torder.deleted = 0
AND torder.`status` IN (7, 8, 9)

	AND torder.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


	AND torder.`code` = #{orderNo,jdbcType=VARCHAR}


	AND tuser.nickname = #{userName,jdbcType=VARCHAR}


	AND tprod.`name` = #{productName,jdbcType=VARCHAR}


	AND torder.`status` = #{orderState,jdbcType=VARCHAR}

SELECT tuser.nickname AS userNickName, tuser.open_id AS openId, torder.`code` AS orderNo, tprod.`code` AS productCode, tprod.`name` AS productName, torder.buy_amount AS buyAmount, torder.`status` AS orderStatus, torder.sell_amount AS sellAmount, torder.create_time AS createTime FROM t_order AS torder LEFT JOIN t_product AS tprod ON torder.product_id = tprod.id LEFT JOIN t_user AS tuser ON tuser.id = torder.user_id WHERE torder.deleted = 0 AND torder.`status` IN (7, 8, 9)
AND torder.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
AND torder.`code` = #{orderNo,jdbcType=VARCHAR}
AND tuser.nickname = #{userName,jdbcType=VARCHAR}
AND tprod.`name` = #{productName,jdbcType=VARCHAR}
AND torder.`status` = #{orderState,jdbcType=VARCHAR}
ORDER BY torder.create_time desc

SELECT
	count(selfOrder.id)
FROM
t_order AS selfOrder
LEFT JOIN t_user AS selfUser ON selfOrder.user_id = selfUser.id
LEFT JOIN t_order AS toOrder ON toOrder.source_order_id = selfOrder.id
LEFT JOIN t_user AS toUser ON toOrder.user_id = toUser.id
LEFT JOIN t_product AS selfProd ON selfOrder.product_id = selfProd.id
WHERE
selfOrder.deleted = 0
AND selfOrder.`status` IN (3, 4, 5, 6)

	AND selfOrder.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


	AND selfOrder.`code` = #{orderNo,jdbcType=VARCHAR}


	AND selfOrder.nickname = #{userName,jdbcType=VARCHAR}


	AND selfOrder.`name` = #{productName,jdbcType=VARCHAR}


	AND selfOrder.`status` = #{orderState,jdbcType=VARCHAR}

SELECT selfOrder.id AS orderId, selfUser.nickname AS fromUserNickName, selfUser.open_id AS openId, selfOrder.`code` AS orderNo, selfOrder.buy_amount AS buyAmount, selfOrder.`status` AS orderStatus, selfOrder.create_time AS createTime, toOrder.create_time AS receiveTime, selfProd.`code` AS productCode, selfProd.`name` AS productName, toUser.nickname AS toUserNickName FROM t_order AS selfOrder LEFT JOIN t_user AS selfUser ON selfOrder.user_id = selfUser.id LEFT JOIN t_order AS toOrder ON toOrder.source_order_id = selfOrder.id LEFT JOIN t_user AS toUser ON toOrder.user_id = toUser.id LEFT JOIN t_product AS selfProd ON selfOrder.product_id = selfProd.id WHERE selfOrder.deleted = 0 AND selfOrder.`status` IN (3, 4, 5, 6)
AND selfOrder.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
AND selfOrder.`code` = #{orderNo,jdbcType=VARCHAR}
AND selfOrder.nickname = #{userName,jdbcType=VARCHAR}
AND selfOrder.`name` = #{productName,jdbcType=VARCHAR}
AND selfOrder.`status` = #{orderState,jdbcType=VARCHAR}
ORDER BY selfOrder.create_time desc

